/**
 * extract_file_id (uses ONLY $items('start'))
 * - Reads Telegram update strictly from $items('start')[0].json
 * - Priority: largest photo ‚Üí document ‚Üí video ‚Üí animation ‚Üí sticker
 * - Returns: { chat_id, file_id, caption, file_name, content_kind, has_media, token }
 */

function largestPhoto(photos) {
  if (!Array.isArray(photos) || photos.length === 0) return null;
  const sorted = [...photos].sort((a, b) => (a.file_size || 0) - (b.file_size || 0));
  return sorted.at(-1) || null;
}

function firstText(...vals) {
  for (const v of vals) if (typeof v === 'string' && v.trim()) return v;
  return '';
}

// 1) Strictly use the Start node payload
const startItem = $items('Start')?.[0]?.json || {};

// 2) Pick the effective Telegram message object from Start
//    (covers common Telegram update shapes)
const m =
  startItem.message ||
  startItem.edited_message ||
  startItem.channel_post ||
  startItem.edited_channel_post ||
  (startItem.callback_query ? startItem.callback_query.message : null) ||
  {};

// 3) Extract chat_id
const chat_id =
  (m.chat && typeof m.chat.id !== 'undefined') ? m.chat.id :
  (startItem.callback_query && startItem.callback_query.from && typeof startItem.callback_query.from.id !== 'undefined')
    ? startItem.callback_query.from.id
    : null;

// 4) Extract media file_id by priority
let file_id = null;
let content_kind = null;

const ph = largestPhoto(m.photo);
if (ph?.file_id) {
  file_id = ph.file_id;
  content_kind = 'photo';
} else if (m.document?.file_id) {
  file_id = m.document.file_id;
  content_kind = 'document';
} else if (m.video?.file_id) {
  file_id = m.video.file_id;
  content_kind = 'video';
} else if (m.animation?.file_id) {
  file_id = m.animation.file_id;
  content_kind = 'animation';
} else if (m.sticker?.file_id) {
  file_id = m.sticker.file_id;
  content_kind = 'sticker';
}

// 5) Caption/text and a reasonable file name
const caption = firstText(m.caption, m.text);
const file_name =
  m?.document?.file_name ||
  m?.video?.file_name ||
  (content_kind === 'photo' ? `photo_${Date.now()}.jpg` : `file_${Date.now()}`);

// 6) Read token ONLY from init_config
const token = $items('init_config')?.[0]?.json?.TELEGRAM_BOT_TOKEN || null;

// 7) Output
return [{
  json: {
    chat_id,
    file_id,
    caption,
    file_name,
    content_kind,
    has_media: !!file_id,
    token
  }
}];

/**
 * normalize_for_notion (v3.8)
 * - –ë–µ–∑ —Ö–∞—Ä–¥–∫–æ–¥–∞: –±–µ—Ä—ë—Ç init_config + –≤—Å—ë –∏–∑ –∞–ø—Å—Ç—Ä–∏–º–∞ (LLM/—Å–µ—Ä–≤–∏—Å–Ω—ã–µ –Ω–æ–¥—ã), —Å–ª–∏–≤–∞–µ—Ç –∏ –Ω–æ—Ä–º–∞–ª–∏–∑—É–µ—Ç.
 * - –ü–æ–¥–¥–µ—Ä–∂–∫–∞ UID –∏–∑ –Ω–æ–¥—ã Crypto/crypto (ImageUID/UID/hash/sha256/sha1/md5) ‚Üí –ø–æ—Ç–æ–º UID/file_unique_id ‚Üí fallback-—Ö—ç—à.
 * - –†–µ–∑–æ–ª–≤–∏—Ç screenshot_url (tg/gdrive/llm), –ø—Ä–æ–∫–∏–¥—ã–≤–∞–µ—Ç rootFolderId, ScreenShotFile.
 * - –ü—Ä–æ–∫–∏–¥—ã–≤–∞–µ—Ç –≤–Ω–∏–∑ NOTION_DATABASE_ID (–∏–∑ pick_db_by_name ‚Üí found_db_id), –∞ —Ç–∞–∫–∂–µ database_id/db_id –∞–ª–∏–∞—Å—ã.
 * - –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç –ø–ª–æ—Å–∫–∏–π JSON + init_config.
 */

/************ Small utils ************/
const nonEmpty = v => (v!==null && v!==undefined && (typeof v!=='string' || v.trim()!=='') && (!Array.isArray(v) || v.length>0));
const isObj = v => v && typeof v === 'object' && !Array.isArray(v);
const toStr = v => (v==null ? '' : String(v));
function safe(getter, dflt){ try { const v = getter(); return v === undefined ? dflt : v; } catch { return dflt; } }
function hashStr(s){ s = String(s || ''); let h = 5381; for (let i=0;i<s.length;i++) h = ((h<<5)+h)+s.charCodeAt(i); return (h>>>0).toString(16); }

function toNumber(v){
  if (v == null || v === '') return undefined;
  if (typeof v === 'number' && isFinite(v)) return v;
  const s = String(v).trim().replace(/\s+/g, '').replace(',', '.');
  const n = Number(s);
  return isFinite(n) ? n : undefined;
}
function upper(v){ if (!nonEmpty(v) && v !== 0) return undefined; return String(v).trim().toUpperCase(); }

/************ Dates ************/
function toIsoDate(v){
  if (!v) return undefined;
  if (typeof v === 'string'){
    const s = v.trim();
    if (/^\d{4}-\d{2}-\d{2}/.test(s)) return s.slice(0,10);
    const dm = s.match(/^(\d{2})\.(\d{2})\.(\d{4})$/);
    if (dm) return `${dm[3]}-${dm[2]}-${dm[1]}`;
    const d = new Date(s);
    if (!isNaN(+d)) return d.toISOString().slice(0,10);
    return undefined;
  }
  if (v instanceof Date && !isNaN(+v)) return v.toISOString().slice(0,10);
  return undefined;
}
function buildNotionDate(input){
  if (!input) return undefined;
  if (typeof input === 'string' || input instanceof Date){
    const start = toIsoDate(input);
    return start ? { start, end: null } : undefined;
  }
  const start = toIsoDate(input.start);
  const end = toIsoDate(input.end);
  if (!start && !end) return undefined;
  return { start: start || undefined, end: end || null };
}

/************ Lists / bool ************/
function toMulti(val){
  if (val == null) return undefined;
  if (Array.isArray(val)) return val.filter(nonEmpty).map(v=>String(v).trim());
  const s = String(val);
  if (s.includes(',')) return s.split(',').map(x=>x.trim()).filter(nonEmpty);
  const one = s.trim();
  return one ? [one] : undefined;
}
function boolish(v){
  if (typeof v === 'boolean') return v;
  const s = String(v).trim().toLowerCase();
  if (['true','yes','1','y','on'].includes(s)) return true;
  if (['false','no','0','n','off'].includes(s)) return false;
  return !!v;
}

/************ Flatten + merge ************/
function flatten(obj, out = {}) {
  if (!isObj(obj)) return out;
  for (const [k, v] of Object.entries(obj)) {
    out[k] = v;
    if (isObj(v) && ['data','payload','record','result','results','properties','output'].includes(k)) {
      for (const [kk, vv] of Object.entries(v)) if (out[kk] === undefined) out[kk] = vv;
    }
  }
  return out;
}
function mergedData(){
  const all = $input.all() || [];
  const sources = [
    ...all.map(i => i.json),
    $json,
    safe(()=> $items('gd_output')[0].json, null),
    safe(()=> $items('build_tgURL')[0].json, null),
    safe(()=> $items('getFile')[0].json, null),
  ].filter(Boolean);
  const m = {};
  for (const s of sources) flatten(s, m);
  return m;
}

/************ Init config ************/
function pickInit(){
  return safe(()=> $items('init_config')[0].json, null)
      || safe(()=> $json.init_config, null)
      || null;
}
const cfg = pickInit() || {};
const fieldsCfgArray = Array.isArray(cfg.fields) ? cfg.fields : []; // –µ—Å–ª–∏ –º–∞—Å—Å–∏–≤
const ndbProps = isObj(cfg.NOTION_DB_PROPERTIES) ? cfg.NOTION_DB_PROPERTIES : null; // –µ—Å–ª–∏ –æ–±—ä–µ–∫—Ç —Å—Ö–µ–º—ã
const allowedTypes = safe(()=> (cfg.typeOptions?.Type?.select?.options || []).map(o => o.name), []);
const SCREEN_ROOT_ID = cfg.routes?.screenRootId || '1TD-SOF7vE5EnYwhwils2GFIFT5mQwlAP';

/************ DB ID resolver (pick_db_by_name first) ************/
function pickDbIdFromFlow(){
  const byName = safe(()=> $items('pick_db_by_name')[0].json, null);
  if (byName?.found_db_id) return byName.found_db_id;
  if ($json?.NOTION_DATABASE_ID) return $json.NOTION_DATABASE_ID;
  if ($json?.database_id)       return $json.database_id;
  if ($json?.db_id)             return $json.db_id;
  if (cfg?.database_id)         return cfg.database_id;
  const all = $input.all() || [];
  for (const it of all){
    const j = it.json || {};
    if (j.found_db_id) return j.found_db_id;
    if (j.database_id) return j.database_id;
    if (j.db_id)       return j.db_id;
  }
  return null;
}

/************ Screenshot candidates (LLM/TG/GDrive) ************/
const file_name =
  safe(()=> $node['build_tgURL'].json.file_name, null) ||
  safe(()=> $items('build_tgURL')[0].json.file_name, null) ||
  safe(()=> $json.file_name, 'file');

const token =
  safe(()=> $node['extract_file_id'].json.token, null) ||
  safe(()=> $items('extract_file_id')[0].json.token, null);

function pickScreenshotURL(data){
  const cand = [
    data.screenshot_url,
    data.ScreenshotUrl,
    data.preview_url,
    data.image_url,
    data.file_url,
    data.thumb_url,
    data.gdrive_view_url,
  ].filter(nonEmpty);

  const tg_direct =
    safe(()=> $items('build_tgURL')[0].json.tg_file_url, null) ||
    (token && safe(()=> $items('getFile')[0].json.result.file_path, null)
      ? `https://api.telegram.org/file/bot${token}/${$items('getFile')[0].json.result.file_path}`
      : null) ||
    (token && data.file_path ? `https://api.telegram.org/file/bot${token}/${data.file_path}` : null);

  if (tg_direct) cand.unshift(tg_direct);

  if (nonEmpty(data.gdrive_file_id)){
    const id = data.gdrive_file_id;
    cand.unshift(`https://drive.google.com/uc?export=view&id=${id}`);
    cand.unshift(`https://drive.google.com/thumbnail?id=${id}&sz=w2048`);
  }

  const uniq = [...new Set(cand)];
  return uniq[0] || undefined;
}

/************ UID resolver (Crypto node / data / fallback) ************/
function pickUID(data){
  const cryptoJ =
    safe(()=> $items('Crypto')[0].json, null) ||
    safe(()=> $items('crypto')[0].json, null) ||
    safe(()=> $node['Crypto'].json, null)     ||
    safe(()=> $node['crypto'].json, null);

  const fromCrypto =
    cryptoJ?.ImageUID || cryptoJ?.imageUID ||
    cryptoJ?.UID      || cryptoJ?.uid      ||
    cryptoJ?.hash     || cryptoJ?.sha256   ||
    cryptoJ?.sha1     || cryptoJ?.md5      || null;

  const fromData =
    data.UID || data.uid ||
    data.imageUID || data.image_uid ||
    data.file_unique_id || data.file_id || null;

  if (fromCrypto) return String(fromCrypto);
  if (fromData)   return String(fromData);

  const raw = [
    data['Source URL'] || data.source_url || '',
    data.screenshot_url || data.ScreenshotUrl || '',
    data.file_name || data.ScreenShotFile || '',
    data.file_size || '',
    data.Title || data.title || ''
  ].join('|');
  return hashStr(raw);
}

/************ Type inference ************/
function inferType(name, value){
  const s = String(name||'').toLowerCase();
  if (s.includes('url') || /^https?:\/\//i.test(value||'')) return 'url';
  if (s.endsWith('_email') || s === 'email') return 'email';
  if (s.endsWith('_phone') || s === 'phone') return 'phone_number';
  if (s.includes('date') || s.endsWith('_at') || s.endsWith('date')) return 'date';
  if (s.includes('price') || s.includes('amount') || typeof value === 'number') return 'number';
  if (s.includes('tags') || s.includes('labels') || Array.isArray(value)) return 'multi_select';
  if (s.includes('currency') || s.includes('status') || s.endsWith('type') || s.includes('category') || s.includes('lang') || s.includes('channel')) return 'select';
  if (s.includes('screenshot') || s.endsWith('_file') || s.endsWith('_image')) return 'files';
  if (s.includes('checkbox') || typeof value === 'boolean') return 'checkbox';
  if (s === 'title' || s === 'name') return 'title';
  return 'rich_text';
}
function normalizeByType(t, val){
  switch (t) {
    case 'number': return toNumber(val);
    case 'date':
      if (val && typeof val === 'object' && ('start' in val || 'end' in val)) return buildNotionDate(val);
      return buildNotionDate(val) || undefined;
    case 'select': return nonEmpty(val) ? toStr(val) : undefined;
    case 'multi_select': return toMulti(val);
    case 'url': return nonEmpty(val) ? toStr(val) : undefined;
    case 'email': return nonEmpty(val) ? toStr(val) : undefined;
    case 'phone_number': return nonEmpty(val) ? toStr(val) : undefined;
    case 'checkbox': return !!boolish(val);
    case 'files': return nonEmpty(val) ? toStr(val) : undefined; // URL string
    case 'title': return nonEmpty(val) ? toStr(val) : undefined;
    default: return nonEmpty(val) ? toStr(val) : undefined;
  }
}

/************ Build normalized payload ************/
const data = mergedData();
const out = {};
const usedKeys = new Set();

// 0) –ï—Å–ª–∏ init_config —Å–æ–¥–µ—Ä–∂–∏—Ç –æ–±—ä–µ–∫—Ç —Å—Ö–µ–º—ã NOTION_DB_PROPERTIES ‚Äî –ø–æ—Å—Ç—Ä–æ–∏–º –ª–æ–∫–∞–ª—å–Ω—É—é –∫–∞—Ä—Ç—É —Ç–∏–ø–æ–≤ (–ø–æ –∂–µ–ª–∞–Ω–∏—é)
const cfgMap = {};
if (ndbProps){
  for (const [k, schema] of Object.entries(ndbProps)){
    if (!isObj(schema)) continue;
    const [t] = Object.keys(schema); // title / select / number / date / ...
    if (t) cfgMap[k] = t;
  }
}

// 1) –°–Ω–∞—á–∞–ª–∞ ‚Äî –ø–æ–ª—è –∏–∑ init_config.fields (–µ—Å–ª–∏ —ç—Ç–æ –º–∞—Å—Å–∏–≤ –∏–º—ë–Ω/–æ–ø–∏—Å–∞–Ω–∏–π –ø–æ–ª–µ–π)
for (const f of (fieldsCfgArray || [])){
  const name = f?.name || f?.notion || f?.src;
  if (!name) continue;
  const src  = f?.src || name;
  const raw  = (data[src] !== undefined) ? data[src] : data[name];
  const type = f?.type || cfgMap[name] || inferType(name, raw);

  let v = raw;
  if ((!nonEmpty(v)) && type === 'files' && /screenshot/i.test(name)) v = pickScreenshotURL(data);

  const norm = normalizeByType(type, v);
  if (norm !== undefined){ out[name] = norm; usedKeys.add(src); usedKeys.add(name); }
}

// 2) –ï—Å–ª–∏ –∏–∑ –ø—É–Ω–∫—Ç–∞ (1) –Ω–∏—á–µ–≥–æ –Ω–µ –ø—Ä–∏—à–ª–æ ‚Äî –æ—Ä–∏–µ–Ω—Ç–∏—Ä—É–µ–º—Å—è –Ω–∞ ndbProps (–æ–±—ä–µ–∫—Ç —Å—Ö–µ–º—ã)
if (Object.keys(out).length === 0 && Object.keys(cfgMap).length){
  for (const [name, type] of Object.entries(cfgMap)){
    const raw = (data[name] !== undefined) ? data[name] : data[name];
    let v = raw;
    if ((!nonEmpty(v)) && type === 'files' && /screenshot/i.test(name)) v = pickScreenshotURL(data);
    const norm = normalizeByType(type, v);
    if (norm !== undefined){ out[name] = norm; usedKeys.add(name); }
  }
}

// 3) Title (–µ—Å–ª–∏ –Ω–µ –∑–∞–¥–∞–Ω)
if (out.Title == null){
  const titleCand = data.Title ?? data.title ?? data.Name ?? data.name ?? data.caption ?? 'Untitled';
  out.Title = toStr(titleCand);
  usedKeys.add('Title'); usedKeys.add('title'); usedKeys.add('Name'); usedKeys.add('name');
}

// 4) Type (–æ–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º —Å–ø–∏—Å–∫–æ–º –∏–∑ init_config, –µ—Å–ª–∏ –æ–Ω –µ—Å—Ç—å)
if (allowedTypes?.length){
  const proposed = data.Type ?? data.type ?? 'Note';
  out.Type = allowedTypes.includes(proposed) ? proposed : 'Note';
  usedKeys.add('Type'); usedKeys.add('type');
} else if (data.Type || data.type){
  out.Type = toStr(data.Type ?? data.type);
  usedKeys.add('Type'); usedKeys.add('type');
}

// 5) UID (Crypto ‚Üí data ‚Üí fallback hash)
out['UID'] = pickUID(data);

// 6) –ê–≤—Ç–æ–¥–æ–±–∞–≤–ª–µ–Ω–∏–µ –æ—Å—Ç–∞–ª—å–Ω—ã—Ö –∫–ª—é—á–µ–π (–∏–Ω—Ñ–µ—Ä–µ–Ω—Å)
const SKIP = new Set([
  'id','object','url','cover','icon','parent','archived','created_time','last_edited_time',
  'properties','results','result','payload','data','output','method','body','headers',
  'database_id','db_id','found_db_id','found_db_url','found_title','requested_name',
  'file_id','file_path','tg_file_url','token',
  'gdrive_file_id','gdrive_view_url',
]);
for (const [k,v] of Object.entries(data)){
  if (usedKeys.has(k) || SKIP.has(k)) continue;
  if (!nonEmpty(v)) continue;
  if (out[k] !== undefined) continue;
  const type = cfgMap[k] || inferType(k, v);
  const norm = normalizeByType(type, v);
  if (norm !== undefined) out[k] = norm;
}

// 7) Screenshot/screenshot_url (–µ—Å–ª–∏ –µ—â—ë –Ω–µ—Ç)
const bestShot = pickScreenshotURL({ ...data, ScreenshotUrl: out.Screenshot ?? data.ScreenshotUrl });
if (bestShot && !out.Screenshot && !out.screenshot_url){
  const hasScreenshotProp =
    (fieldsCfgArray || []).some(f => (f?.type === 'files' && /screenshot/i.test(f?.name || f?.src || ''))) ||
    Object.keys(cfgMap).some(k => /screenshot/i.test(k) && cfgMap[k]==='files');
  if (hasScreenshotProp) out['Screenshot'] = bestShot;
  else out['screenshot_url'] = bestShot;
}

// 8) –õ—ë–≥–∫–∞—è –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è —á–∞—Å—Ç—ã—Ö –ø–æ–ª–µ–π
if (out.Currency != null) out.Currency = upper(out.Currency);
if (data.currency != null && out.Currency == null) out.Currency = upper(data.currency);
if (!out['Map URL'] && (out.Address || data.address)){
  out['Map URL'] = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(out.Address || data.address)}`;
}

// 9) –°–µ—Ä–≤–∏—Å –¥–ª—è GDrive –∏ —Ç–∞–π–º—à—Ç–∞–º–ø
out['ScreenShotFile'] = file_name || 'file';
out['rootFolderId']   = SCREEN_ROOT_ID;
out['Captured at']    = new Date().toISOString();

// 10) –ü—Ä–æ–ª–æ–∂–∏–º init_config –≤–Ω–∏–∑
out['init_config'] = cfg;

// 11) –ü—Ä–æ–∫–∏–Ω–µ–º NOTION_DATABASE_ID (–∏–∑ pick_db_by_name –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–Ω–æ)
const DB_ID = pickDbIdFromFlow();
if (DB_ID){
  out.NOTION_DATABASE_ID = DB_ID;
  out.database_id        = DB_ID;
  out.db_id              = DB_ID;
}

// Done
return [{ json: out }];

/**
 * build_tg_message (safe)
 * –†–∞–±–æ—Ç–∞–µ—Ç –ø–æ—Å–ª–µ pick_a_page (Append).
 * –î–ª—è Update –≤—Ö–æ–¥–Ω—ã—Ö items 2 ‚Üí –±–µ—Ä—ë–º –ø–æ—Å–ª–µ–¥–Ω–∏–π; –¥–ª—è Create 1.
 * –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç { text, chat_id, notion_url, notion_page_id, action, ... }.
 */

function escMd(s) {
  // –ª—ë–≥–∫–∏–π —ç—Å–∫–µ–π–ø –ø–æ–¥ Markdown/MarkdownV2
  return String(s ?? '').replace(/([_*[\]()~`>#+\-=|{}.!\\])/g, '\\$1');
}
function safeItems(name, outIdx = 0) {
  try { return $items(name, 0, outIdx) || []; } catch { return []; }
}

// 1) –æ–ø—Ä–µ–¥–µ–ª–∏–º –¥–µ–π—Å—Ç–≤–∏–µ –ø–æ –∫–æ–ª–∏—á–µ—Å—Ç–≤—É –≤—Ö–æ–¥–Ω—ã—Ö items
const inputPages = items.map(i => i.json).filter(p => p && p.object === 'page');
const page = inputPages[inputPages.length - 1] || null; // –ø–æ—Å–ª–µ–¥–Ω–∏–π ‚Äî –Ω—É–∂–Ω—ã–π
const isUpdate = inputPages.length > 1 || safeItems("Update a page").length > 0;
const isCreate = !isUpdate;

// 2) chat_id –∏–∑ –ø–µ—Ä–≤–æ–≥–æ —É–∑–ª–∞
const chat_id = (safeItems("extract_file_id")[0]?.json?.chat_id) ?? null;

if (!page) {
  return [{ json: { text: "‚ö†Ô∏è –ü—É—Å—Ç–æ–π –æ—Ç–≤–µ—Ç –æ—Ç Notion.", chat_id, notion_url: null, notion_page_id: null } }];
}

const props = page.properties || {};
const title = props?.Title?.title?.[0]?.plain_text ?? 'Untitled';
const type  = props?.Type?.select?.name ?? 'Note';
const tagsArr = (props?.Tags?.multi_select ?? []).map(t => t?.name).filter(Boolean);
const tagsStr = tagsArr.join(', ');
const summary = (props?.Summary?.rich_text ?? []).map(r => r?.plain_text ?? '').join(' ').trim();
const srcUrl  = props?.["Source URL"]?.url ?? null;

const d1 = props?.["event_DateStart"]?.date?.start ?? null;
const d2 = props?.["event_DateEnd"]?.date?.end ?? null;
const dateLine = (d1 || d2)
  ? `üóì ${escMd((d1 || '').slice(0, 10))}${d2 ? '‚Äì' + escMd(d2.slice(0, 10)) : ''}`
  : null;

const notionUrl = page.url || null;
const action = isUpdate ? '‚úèÔ∏è –û–±–Ω–æ–≤–ª–µ–Ω–æ' : 'üÜï –°–æ–∑–¥–∞–Ω–æ';

// 3) —Ç–µ–∫—Å—Ç –¥–ª—è Telegram (Markdown)
const lines = [
  `${action}: *${escMd(title)}*`,
  `–¢–∏–ø: ${escMd(type)}${tagsStr ? ` | üè∑ ${escMd(tagsStr)}` : ''}`,
