// === build_create_payload (Simplified) ===
// Create Notion page dynamically from simplified init_config + merged data.
// Only includes fields that exist in the database schema.

/************ Helpers ************/
const nonEmpty = v => (v!==null && v!==undefined && (typeof v!=='string' || v.trim()!=='') && (!Array.isArray(v) || v.length>0));
const toStr = v => (v==null ? '' : String(v));
const toNum = v => {
  if (typeof v === 'number' && isFinite(v)) return v;
  const n = Number(String(v).replace(/\s+/g,'').replace(',', '.'));
  return isFinite(n) ? n : undefined;
};
const toISODate = v => {
  if (!v) return undefined;
  if (typeof v === 'string' && /^\d{4}-\d{2}-\d{2}/.test(v)) return v.slice(0,10);
  if (typeof v === 'string') {
    const m = v.match(/^(\d{2})\.(\d{2})\.(\d{4})$/);
    if (m) return `${m[3]}-${m[2]}-${m[1]}`;
  }
  const d = new Date(v);
  return isNaN(+d) ? undefined : d.toISOString().slice(0,10);
};
const arr = v => Array.isArray(v) ? v : (v==null ? [] : [v]);
const isObj = v => v && typeof v === 'object' && !Array.isArray(v);

function inferType(name, value){
  const s = String(name||'').toLowerCase();
  if (s.includes('url') || /^https?:\/\//i.test(value||'')) return 'url';
  if (s.endsWith('_email') || s === 'email') return 'email';
  if (s.endsWith('_phone') || s === 'phone') return 'phone_number';
  if (s.includes('date')) return 'date';
  if (s.includes('price') || typeof value === 'number') return 'number';
  if (s.includes('tags') || Array.isArray(value)) return 'multi_select';
  if (s.includes('currency') || s.includes('status') || s.includes('type') || s.includes('category') || s.includes('lang') || s.includes('channel')) return 'select';
  if (s.includes('screenshot') || s.endsWith('_file') || s.endsWith('_image')) return 'files';
  if (s.includes('checkbox') || typeof value === 'boolean') return 'checkbox';
  return 'rich_text';
}

function toMulti(val){
  if (Array.isArray(val)) return val.filter(nonEmpty).map(toStr);
  if (typeof val === 'string' && val.includes(',')) return val.split(',').map(s=>s.trim()).filter(nonEmpty);
  return [toStr(val)];
}

function flatten(obj, out = {}) {
  if (!isObj(obj)) return out;
  for (const [k, v] of Object.entries(obj)) {
    out[k] = v;
    if (isObj(v) && ['data','payload','record','result','results','properties','output'].includes(k)) {
      for (const [kk, vv] of Object.entries(v)) if (out[kk] === undefined) out[kk] = vv;
    }
  }
  return out;
}

/************ Pickers ************/
function pickInit(){
  return $json.init_config
      || ($items('init_config')?.[0]?.json)
      || ($items('normalize_for_notion')?.[0]?.json?.init_config)
      || null;
}

function mergedData(){
  const all = $input.all() || [];
  const sources = [
    $items('normalize_for_notion')?.[0]?.json,
    $json,
    ...all.map(i => i.json),
    $items('gd_output')?.[0]?.json,
  ].filter(Boolean);
  const m = {};
  for (const s of sources) flatten(s, m);
  return m;
}

function pickDbId(init){
  const norm = $items('normalize_for_notion')?.[0]?.json;
  if (norm?.NOTION_DATABASE_ID) return norm.NOTION_DATABASE_ID;
  if (norm?.database_id)        return norm.database_id;
  if (norm?.db_id)              return norm.db_id;

  const fromPick = $items('pick_db_by_name')?.[0]?.json;
  if (fromPick?.found_db_id) return fromPick.found_db_id;

  if (init?.database_id) return init.database_id;

  const fallbacks = [
    $items('find_database_by_name')?.[0]?.json,
    $items('get_db_by_name')?.[0]?.json,
    $items('db_lookup')?.[0]?.json,
    $json
  ].filter(Boolean);
  for (const j of fallbacks){
    const id = j.found_db_id || j.database_id || j.db_id || j.id;
    if (id) return id;
  }
  for (const it of ($input.all() || [])){
    const j = it.json || {};
    const id = j.found_db_id || j.database_id || j.db_id || j.id;
    if (id) return id;
  }
  return null;
}

function pickImageUrls(r){
  const candidates = [
    r.gdrive_view_url,
    r.ScreenshotUrl,
    r.screenshot_url,
    r.preview_url,
    r.thumb_url,
    r.image_url,
    r.file_url
  ].filter(nonEmpty);

  if (nonEmpty(r.gdrive_file_id)){
    const id = r.gdrive_file_id;
    candidates.unshift(`https://drive.google.com/uc?export=view&id=${id}`);
    candidates.unshift(`https://drive.google.com/thumbnail?id=${id}&sz=w2048`);
  }
  const uniq = [...new Set(candidates)];
  const coverUrl = uniq.find(u => /thumbnail\?id=|sz=w/i.test(u)) || uniq[0] || null;
  const shotUrl  = uniq[0] || null;
  return { coverUrl, shotUrl };
}

/************ Inputs ************/
const init = pickInit() || {};
const data = mergedData();
const DB_ID = pickDbId(init);

if (!DB_ID) {
  return [{ json: { _error: "NO_DATABASE_ID", _hint: "Expected NOTION_DATABASE_ID from normalize_for_notion or found_db_id from pick_db_by_name" } }];
}

const { coverUrl, shotUrl } = pickImageUrls(data);

// Get structure from simplified init_config
const dbStructure = init.DB_STRUCTURE || {};
const validFields = dbStructure.validFields || [];
const fieldTypes = dbStructure.fieldTypes || {};
const requiredFields = dbStructure.requiredFields || ['Title', 'Type'];
const autoFields = dbStructure.autoFields || {};

/************ Build properties ************/
const P = {};
const fromConfig = new Set();

// 1) Process all valid fields from data
for (const [fieldName, value] of Object.entries(data)) {
  // Skip if field doesn't exist in DB schema
  if (!validFields.includes(fieldName)) {
    console.warn(`Skipping non-existent field: ${fieldName}`);
    continue;
  }

  fromConfig.add(fieldName);

  // Handle auto-generated fields
  if (autoFields[fieldName] && typeof autoFields[fieldName] === 'function') {
    const autoValue = autoFields[fieldName]();
    if (fieldName === 'Captured at') {
      P[fieldName] = { date: { start: autoValue } };
    } else if (fieldName === 'Channel') {
      P[fieldName] = { select: { name: autoValue } };
    }
    continue;
  }

  const fieldType = fieldTypes[fieldName] || inferType(fieldName, value);

  // Skip empty values except for specific types
  if (!nonEmpty(value) && !['files','date','checkbox'].includes(fieldType)) continue;

  switch (fieldType) {
    case 'title':
      P[fieldName] = { title: [{ text: { content: toStr(value) } }] };
      break;
    case 'select':
      P[fieldName] = { select: nonEmpty(value) ? { name: toStr(value) } : null };
      break;
    case 'multi_select':
      P[fieldName] = { multi_select: toMulti(value).map(v => ({ name: v })) };
      break;
    case 'number': {
      const n = toNum(value);
      if (n !== undefined) P[fieldName] = { number: n };
      break;
    }
    case 'date': {
      if (value && typeof value === 'object' && ('start' in value || 'end' in value)) {
        const s = toISODate(value.start); const e = toISODate(value.end);
        if (s || e) P[fieldName] = { date: { start: s || e, end: e || null } };
      } else {
        const s = toISODate(value);
        if (s) P[fieldName] = { date: { start: s } };
      }
      break;
    }
    case 'url':
      if (nonEmpty(value)) P[fieldName] = { url: toStr(value) };
      break;
    case 'email':
      if (nonEmpty(value)) P[fieldName] = { email: toStr(value) };
      break;
    case 'phone_number':
      if (nonEmpty(value)) P[fieldName] = { phone_number: toStr(value) };
      break;
    case 'files': {
      const url = (fieldName === 'Screenshot') ? (shotUrl || toStr(value)) : (toStr(value) || shotUrl);
      if (nonEmpty(url)) P[fieldName] = { files: [{ name: data.ScreenShotFile || 'file', external: { url } }] };
      break;
    }
    case 'checkbox':
      P[fieldName] = { checkbox: !!value };
      break;
    default:
      if (nonEmpty(value)) P[fieldName] = { rich_text: [{ text: { content: toStr(value) } }] };
  }
}

// 2) Ensure required fields are present
for (const requiredField of requiredFields) {
  if (!P[requiredField]) {
    if (requiredField === 'Title') {
      P[requiredField] = { title: [{ text: { content: 'Untitled' } }] };
    } else if (requiredField === 'Type') {
      P[requiredField] = { select: { name: 'Note' } };
    }
  }
}

// 3) Ensure Captured at is set
if (!P['Captured at']) {
  const today = new Date().toISOString().slice(0,10);
  P['Captured at'] = { date: { start: today } };
}

// 4) Add Screenshot if not already set and we have a URL
if (!P['Screenshot'] && nonEmpty(shotUrl)) {
  P['Screenshot'] = { files: [{ name: data.ScreenShotFile || 'screenshot.jpg', external: { url: shotUrl } }] };
}

/************ Return ************/
return [{
  json: {
    method: "POST",
    url: "https://api.notion.com/v1/pages",
    body: {
      parent: { database_id: DB_ID },
      properties: P,
      ...(nonEmpty(coverUrl) ? { cover: { external: { url: coverUrl } } } : {})
    }
  }
}];

