// 1. Generate Notion DB Properties for database creation
function generateNotionProperties(fields) {
  const properties = {};
  fields.forEach(field => {
    const baseProperty = { [field.type]: {} };

    if (field.options && field.options.length > 0) {
      baseProperty[field.type].options = field.options.map(opt => ({ name: opt }));
    }

    if (field.type === 'number') {
      baseProperty[field.type].format = 'number';
    }

    properties[field.name] = baseProperty;
  });
  return properties;
}

// 2. Generate field prompts for AI
function generateFieldPrompts(fields) {
  const prompts = {};
  fields.forEach(field => {
    if (field.prompt) {
      prompts[field.name] = field.prompt;
    }
  });
  return prompts;
}

// 3. Generate DB structure for validation
function generateDBStructure(fields) {
  return {
    validFields: fields.map(f => f.name),
    fieldTypes: Object.fromEntries(fields.map(f => [f.name, f.type])),
    requiredFields: fields.filter(f => f.required).map(f => f.name),
    readOnlyFields: fields.filter(f => f.readOnly).map(f => f.name),
    autoFields: {
      "Captured at": () => new Date().toISOString().slice(0, 10),
      "Channel": () => "Telegram Bot"
    },
    conditionalFields: fields.filter(f => f.showWhen).map(f => ({
      name: f.name,
      condition: f.showWhen
    }))
  };
}

// 4. Generate type-specific field groups
function generateTypeGroups(fields) {
  const groups = {
    Product: {},
    Contact: {},
    Event: {},
    Note: {}
  };

  fields.forEach(field => {
    if (field.showWhen) {
      const type = field.showWhen.replace("Type === '", "").replace("'", "");
      if (groups[type]) {
        groups[type][field.name] = { prompt: field.prompt };
      }
    }
  });

  return groups;
}

// === BUILD FINAL CONFIGURATION ===
const config = {
  // Workflow settings
  ...WORKFLOW_SETTINGS,

  // Generated configurations
  NOTION_DB_PROPERTIES: generateNotionProperties(FIELDS),
  DB_STRUCTURE: generateDBStructure(FIELDS),
  FIELDS: generateFieldPrompts(FIELDS),
  PER_TYPE: generateTypeGroups(FIELDS),

