/**
 * build_notion_create_payload
 * Builds a Notion /v1/databases create body entirely from init_config.
 * Fails early with clear messages if something is missing.
 *
 * Inputs:
 *  - $items('init_config')[0].json:
 *      NOTION_PARENT_PAGE_ID (string, 32-hex or with dashes)
 *      NOTION_DB_NAME        (string)
 *      NOTION_DB_PROPERTIES  (object)  // full schema
 *      NOTION_VERSION        (string)  // e.g. "2022-06-28"
 *
 * Output:
 *  - json: { body, meta } where:
 *      body = ready-to-send request body for POST /v1/databases
 *      meta = { notionVersion, name }
 */

function normId(id) {
  if (!id) return null;
  const clean = String(id).replace(/-/g, '');
  return /^[a-f0-9]{32}$/i.test(clean) ? clean : null;
}

const ini = $items('init_config')[0]?.json || {};
const parentPageRaw = ini.NOTION_PARENT_PAGE_ID;
const parentPageId  = normId(parentPageRaw);

if (!parentPageId) {
  throw new Error("init_config: NOTION_PARENT_PAGE_ID is missing or invalid. Open your parent page and copy 32-hex ID from its URL.");
}

const dbName = (ini.NOTION_DB_NAME || '').trim();
if (!dbName) {
  throw new Error("init_config: NOTION_DB_NAME is required.");
}

const properties = ini.NOTION_DB_PROPERTIES;
if (!properties || typeof properties !== 'object' || Array.isArray(properties)) {
  throw new Error("init_config: NOTION_DB_PROPERTIES must be an object with Notion property definitions.");
}

// Optional sanity: ensure Title exists
if (!properties.Title || !('title' in properties.Title)) {
  throw new Error('init_config: NOTION_DB_PROPERTIES must include a "Title" property of type { "title": {} }.');
}

// Build create body
const body = {
  parent: { type: "page_id", page_id: parentPageId },
  title: [{ type: "text", text: { content: dbName } }],
  properties
};

// Pass Notion-Version via meta (for HTTP node headers)
const notionVersion = ini.NOTION_VERSION || "2022-06-28";

return [{
  json: {
    body,
    meta: { notionVersion, name: dbName, parentPageId }
  }
}];

/**
 * pick_db_by_name
 * Selects a Notion database from /v1/search results using strict rules.
 *
 * Inputs:
 *  - $items('init_config')[0].json:
 *      NOTION_DB_NAME            (string)   // target DB name, e.g. "ScreenSave 3"
 *      NOTION_PARENT_PAGE_ID     (string)   // optional parent page to match against
 *      STRICT_DB_NAME_MATCH      (boolean)  // default: true (exact match)
 *      REQUIRE_PARENT_MATCH      (boolean)  // default: false (also check parent)
 *      ALLOW_CONTAINS_FALLBACK   (boolean)  // default: false (fallback to 'includes')
 *
 *  - $json.results               (array)    // raw response from POST /v1/search
 *
 * Output (one item):
 *  - json: {
 *      db_exist,                   // <-- ADDED: boolean
 *      found_db_id,
 *      found_db_url,
 *      found_title,
 *      found_parent_page_id,
 *      resolved_as,                // 'exact' | 'contains' | null
 *      candidates_seen: [{ id, title, parent_page_id, url }],
 *      inspected_count,
 *      // convenience/debugging:
 *      requested_name,
 *      require_parent_match,
 *      matched_parent,             // boolean | null (null if not applicable)
 *      note                        // optional hint when not found
 *    }
 */

function normId(x) {
  if (!x) return null;
  return String(x).replace(/-/g, '').toLowerCase();
}
function titleOf(db) {
  const t = db?.title?.[0]?.plain_text;
  return (t ? String(t) : '').trim();
}
function parentPageOf(db) {
  if (db?.parent?.type === 'page_id') return db.parent.page_id || null;
  return null;
}

const ini = $items('init_config')[0]?.json ?? {};
const wantName = (ini.NOTION_DB_NAME || '').trim();
const wantNameLC = wantName.toLowerCase();
const strict = ini.STRICT_DB_NAME_MATCH !== false;     // default true
const requireParent = !!ini.REQUIRE_PARENT_MATCH;      // default false
const allowContains = !!ini.ALLOW_CONTAINS_FALLBACK;   // default false
const wantParentNorm = normId(ini.NOTION_PARENT_PAGE_ID);

const results = Array.isArray($json.results) ? $json.results : [];
const onlyDB = results.filter(r => r && r.object === 'database');

// Lightweight view of candidates for logs/diagnostics
const candidates_seen = onlyDB.map(db => ({
  id: db.id,
  title: titleOf(db),
  parent_page_id: parentPageOf(db),
  url: db.url || null
}));

function parentMatches(db) {
  if (!requireParent) return true;
  const p = parentPageOf(db);
  return normId(p) === wantParentNorm;
}

// 1) Exact match first (recommended)
let chosen = null;
if (strict) {
  chosen = onlyDB.find(db =>
    titleOf(db).toLowerCase() === wantNameLC && parentMatches(db)
  );
}

// 2) Optional contains fallback (either explicitly allowed or if strict disabled)
let resolved_as = null;
if (chosen) {
  resolved_as = 'exact';
} else if (allowContains || !strict) {
  chosen = onlyDB.find(db =>
    titleOf(db).toLowerCase().includes(wantNameLC) && parentMatches(db)
  );
  if (chosen) resolved_as = 'contains';
}

// Derive booleans / hints
const db_exists = !!chosen;
const matched_parent = db_exists
  ? (requireParent ? normId(parentPageOf(chosen)) === wantParentNorm : null)
  : null;

let note = undefined;
if (!db_exists) {
  if (!wantName) {
    note = 'NOTION_DB_NAME is empty; cannot select a database.';
  } else if (requireParent && wantParentNorm && onlyDB.some(db => titleOf(db).toLowerCase() === wantNameLC)) {
    note = 'Exact name found but parent did not match (REQUIRE_PARENT_MATCH=true).';
  } else if (strict && allowContains === false) {
    note = 'No exact match found and contains-fallback is disabled.';
  } else {
    note = 'No matching database found.';
  }
}

// Prepare output
return [{
  json: {
    db_exists,                                // <‚Äî used by the next node
    found_db_id: chosen?.id || null,
    found_db_url: chosen?.url || null,
    found_title: chosen ? titleOf(chosen) : null,
    found_parent_page_id: chosen ? parentPageOf(chosen) : null,
    resolved_as, // 'exact' | 'contains' | null
    candidates_seen,
    inspected_count: onlyDB.length,
    // convenience/debugging
    requested_name: wantName,
    require_parent_match: requireParent,
    matched_parent,
    note
  }
}];

// build_error_text
// Handles two cases for your 4-way Switch:
// 1) /start & DB exists  ‚Üí say "already connected"
// 2) not /start & no DB   ‚Üí say "please run /start"
//
// Assumes the workflow provides:
//  - init_config      ‚Üí $items('init_config')[0].json
//  - compose_db_ctx   ‚Üí $items('compose_db_route_ctx')[0].json  (db_exists, database_id, database_url)
//  - Telegram Trigger message in $json.message

const ini   = $items('init_config')[0]?.json ?? {};
const ctx   = $items('compose_db_route_ctx')[0]?.json ?? {};
const msg   = $json.message || {};
const textIn = (msg.text || msg.caption || '').trim();

const isStart = textIn.startsWith('/start');
const hasDb   = !!ctx.db_exists;

const dbName  = ini.NOTION_DB_NAME || 'ScreenSave';
const dbId    = ctx.database_id || null;
const dbUrl   = ctx.database_url || null;

let text;

// Case 1: /start & DB exists
if (isStart && hasDb) {
  text = [
    '‚úÖ <b>Database already connected</b>',
    `<b>Name:</b> ${dbName}`,
    dbId ? `<b>ID:</b> <code>${dbId}</code>` : null,
    dbUrl ? `<a href="${dbUrl}">Open in Notion</a>` : null
  ].filter(Boolean).join('\n');

// Case 2: not /start & no DB
} else if (!isStart && !hasDb) {
  text = [
    '‚ö†Ô∏è <b>Database is not set up yet</b>',
    `This workspace doesn‚Äôt have <b>${dbName}</b>.`,
    'Send <code>/start</code> to create and connect it.',
  ].join('\n');

// Fallback (if node was wired to an unexpected branch)
} else {
  text = '‚ÑπÔ∏è Nothing to report.';
}

return [{
  json: {
    chat_id: msg?.chat?.id,
    text,
    parse_mode: ini.TELEGRAM_PARSE_MODE || 'HTML',
    disable_web_page_preview: true,

    // Optional: uncomment to include an inline ‚Äú/start‚Äù button when DB is missing
    // reply_markup: (!isStart && !hasDb) ? {
    //   inline_keyboard: [[{ text: 'Run /start', callback_data: 'start', }]]
    // } : undefined,
  }
}];

// build_post_create_message
// Purpose: send a friendly confirmation after the Notion DB was created.
//
// Assumptions:
// - This node sits right after your Notion "Create Database" (current item has DB data),
//   OR you have that node named "notion.create_db" and we can read $items('notion.create_db')[0].json.
// - init_config node exists with NOTION_DB_NAME and TELEGRAM_PARSE_MODE.
// - Telegram Trigger node is named "Start" (fallbacks included).
//
// Output:
// - { chat_id, text, parse_mode, disable_web_page_preview, reply_markup }

function firstJsonFrom(nodeName) {
  try {
    const arr = $items(nodeName);
    if (Array.isArray(arr) && arr.length && arr[0]?.json) return arr[0].json;
  } catch (_) {}
  return null;
}

// 1) Config
const ini  = $items('init_config')[0]?.json ?? {};
const parseMode = ini.TELEGRAM_PARSE_MODE || 'HTML';

// 2) Telegram chat (from trigger)
const tg = firstJsonFrom('Start')
       || firstJsonFrom('Telegram Trigger')
       || firstJsonFrom('Telegram')
       || $json;
const chatId =
  tg?.message?.chat?.id ??
  tg?.callback_query?.message?.chat?.id ??
  tg?.channel_post?.chat?.id ??
  tg?.chat?.id;

// 3) DB creation result (current item first, fallback to a named node)
const created = $json?.body || $json; // many Notion nodes return data under body
const createdDb = created?.id
  ? created
  : (firstJsonFrom('notion.create_db') ?? {});

const dbId  = createdDb?.id || createdDb?.database_id || null;
const dbUrl = createdDb?.url || createdDb?.database_url || null;

// 4) Expected name from compose_db_ctx (optional) or init_config
const ctx = firstJsonFrom('compose_db_ctx') ?? {};
const dbName =
  ctx?.expected_name ||
  ini.NOTION_DB_NAME ||
  createdDb?.title?.[0]?.plain_text ||
  'ScreenSave';

// 5) Message text
const lines = [
  'üéâ <b>Database created successfully</b>',
  `<b>Name:</b> ${dbName}`,
  dbId ? `<b>ID:</b> <code>${dbId}</code>` : null,
  dbUrl ? `<a href="${dbUrl}">Open in Notion</a>` : null,
  '',
  'You can now start sending items. Use <code>/help</code> for commands.'
].filter(Boolean);

const text = lines.join('\n');

// 6) Optional inline buttons
const reply_markup = {
  inline_keyboard: [
    ...(dbUrl ? [[{ text: 'Open in Notion', url: dbUrl }]] : []),
    [{ text: 'Add first item', callback_data: 'add_first_item' }]
  ]
};

return [{
  json: {
    chat_id: chatId,
    text,
    parse_mode: parseMode,
    disable_web_page_preview: false,
    reply_markup
  }
}];

/**
 * Function node: extract_json (robust)
 * –î–æ—Å—Ç–∞—ë—Ç JSON –∏–∑ –æ—Ç–≤–µ—Ç–∞ OpenAI –¥–∞–∂–µ –µ—Å–ª–∏ –Ω–µ—Ç –∑–∞–∫—Ä—ã–≤–∞—é—â–∏—Ö ``` –∏/–∏–ª–∏ –æ–±—Ä—ã–≤ –≤ –∫–æ–Ω—Ü–µ.
 * –í—Ö–æ–¥: –ª—é–±–æ–π –æ—Ç–≤–µ—Ç OpenAI; –í—ã—Ö–æ–¥: {‚Ä¶} –∏–ª–∏ {_error, reason, raw, extracted}.
 */

function pickContent(obj) {
  if (obj?.choices?.[0]?.message?.content != null) return obj.choices[0].message.content;
  if (obj?.data?.choices?.[0]?.message?.content != null) return obj.data.choices[0].message.content;
  if (obj?.message?.content != null) return obj.message.content;
  if (obj?.content != null) return obj.content;
  return null;
}

let content = pickContent($json);

// —Å–∫–ª–µ–∏–º –º–∞—Å—Å–∏–≤–Ω—ã–µ –∫—É—Å–∫–∏, –µ—Å–ª–∏ –≤–¥—Ä—É–≥ –ø—Ä–∏—à–ª–æ —á–∞—Å—Ç—è–º–∏
if (Array.isArray(content)) {
  content = content.map(p => typeof p === 'string' ? p : (p?.text ?? p?.content ?? '')).join('');
}
if (typeof content !== 'string' || !content.trim()) {
  return [{ json: { _error: 'No textual content', raw: $json } }];
}

// 1) —É–±–∏—Ä–∞–µ–º –æ—Ç–∫—Ä—ã–≤–∞—é—â–∏–π ```json (–¥–∞–∂–µ –±–µ–∑ –∑–∞–∫—Ä—ã—Ç–∏—è)
let text = content.replace(/^\s*```(?:json|jsonc)?\s*/i, '')
                  .replace(/\s*```+\s*$/i, '') // –µ—Å–ª–∏ –∑–∞–∫—Ä—ã—Ç–∏–µ –µ—Å—Ç—å ‚Äî —Å—Ä–µ–∂–µ–º
                  .replace(/^\uFEFF/, '')
                  .trim();

// 2) –æ—Å—Ç–∞–≤–∏–º —Ç–æ–ª—å–∫–æ —Å –ø–µ—Ä–≤–æ–≥–æ —Å–∏–º–≤–æ–ª–∞ { –∏–ª–∏ [
const start = text.search(/[\[{]/);
if (start >= 0) text = text.slice(start);

// 3) —Å–∞–Ω–∏—Ç–∞–π–∑–µ—Ä: —É–¥–∞–ª—è–µ–º –≤–∏—Å—è—á–∏–µ –∑–∞–ø—è—Ç—ã–µ –∏ –±–∞–ª–∞–Ω—Å–∏—Ä—É–µ–º —Å–∫–æ–±–∫–∏ (–∏–≥–Ω–æ—Ä–∏—Ä—É—è —Ç–æ, —á—Ç–æ –≤–Ω—É—Ç—Ä–∏ —Å—Ç—Ä–æ–∫)
function sanitize(s) {
  // —É–±–µ—Ä—ë–º –≤–∏—Å—è—á—É—é –∑–∞–ø—è—Ç—É—é –ø–µ—Ä–µ–¥ –∑–∞–∫—Ä—ã–≤–∞—é—â–µ–π —Å–∫–æ–±–∫–æ–π
  s = s.replace(/,\s*([}\]])/g, '$1');
  // —É–±–µ—Ä—ë–º —Ö–≤–æ—Å—Ç –≤—Ä–æ–¥–µ ,", ,', , –∏ —Ç.–ø. –≤ —Å–∞–º–æ–º –∫–æ–Ω—Ü–µ
  s = s.replace(/,?\s*["']?\s*$/,'');
  const stack = [];
  let out = '';
  let inStr = false, esc = false;
  for (let i = 0; i < s.length; i++) {
    const ch = s[i];
    out += ch;
    if (esc) { esc = false; continue; }
    if (ch === '\\') { if (inStr) esc = true; continue; }
    if (ch === '"') { inStr = !inStr; continue; }
    if (inStr) continue;
    if (ch === '{' || ch === '[') stack.push(ch);
    else if (ch === '}' || ch === ']') {
      const open = stack.pop();
      if (open && ((open === '{' && ch !== '}') || (open === '[' && ch !== ']'))) {
        out = out.slice(0, -1) + (open === '{' ? '}' : ']');
      }
    }
  }
  while (stack.length) {
    const open = stack.pop();
    out += (open === '{' ? '}' : ']');
  }
  return out.trim();
}

const body = sanitize(text);

try {
  const obj = JSON.parse(body);
  return [{ json: obj }];
} catch (e) {
  return [{ json: { _error: 'JSON parse failed', reason: e.message, raw: content, extracted: body } }];
}

