// compose_db_ctx: builds DB context + routing for Switch

const ini  = $items('init_config')[0]?.json ?? {};
const pick = $items('pick_db_by_name')[0]?.json ?? {};

const database_id   = pick.found_db_id ?? null;
const database_url  = pick.found_db_url ?? null;
const db_exists     = !!pick.db_exists;
const expected_name = ini.NOTION_DB_NAME || 'ScreenSave';

// --- safely read the Telegram trigger payload ---
function firstJsonFrom(nodeName) {
  try {
    const arr = $items(nodeName);
    if (Array.isArray(arr) && arr.length && arr[0]?.json) return arr[0].json;
  } catch (_) {}
  return null;
}

// Try common names you might have on canvas
const tgSource =
  firstJsonFrom('Start') ||
  firstJsonFrom('Telegram Trigger') ||
  firstJsonFrom('Telegram') ||
  firstJsonFrom('is_start_cmd') ||
  $json; // fallback to current item

const textRaw =
  tgSource?.message?.text ??
  tgSource?.message?.caption ??
  tgSource?.callback_query?.data ??
  tgSource?.channel_post?.text ??
  tgSource?.edited_message?.text ??
  '';

const text   = String(textRaw).trim();
// match "/start" or "/start anything"
const isStart = /^\/start(\s|$)/i.test(text);

// --- route (Switch outputs 0..3) ---
// 0: /start & yes DB
// 1: not /start & no DB
// 2: /start & no DB
// 3: not /start & yes DB
const index = isStart ? (db_exists ? 0 : 2) : (db_exists ? 3 : 1);
const label =
  index === 0 ? 'START_HAS_DB' :
  index === 1 ? 'NO_START_NO_DB' :
  index === 2 ? 'START_NO_DB' :
                'NO_START_HAS_DB';

return [{
  json: {
    database_id,
    database_url,
    db_exists,
    resolved_by: pick.resolved_by ?? 'compose_db_ctx',
    expected_name,
    route: { isStart, hasDb: db_exists, index, label, text }
  }
}];

