function generateContactSearchQuery(data) {
  // Priority 1: Title + Address (most specific)
  if (data.Title && data.contact_Address) {
    return `${data.Title} ${data.contact_Address}`;
  }

  // Priority 2: Just address
  if (data.contact_Address) {
    return `${data.contact_Address} location`;
  }

  // Priority 3: Title + generic location
  if (data.Title) {
    return `${data.Title} business location`;
  }

  return null;
}

function generateEventSearchQuery(data) {
  // Priority 1: Title + Address + Date (most specific)
  if (data.Title && data.event_Adddress && data.event_DateStart) {
    return `${data.Title} ${data.event_Adddress} ${data.event_DateStart}`;
  }

  // Priority 2: Title + Address
  if (data.Title && data.event_Adddress) {
    return `${data.Title} ${data.event_Adddress} event`;
  }

  // Priority 3: Title + Date
  if (data.Title && data.event_DateStart) {
    return `${data.Title} ${data.event_DateStart} event`;
  }

  // Priority 4: Just title
  if (data.Title) {
    return `${data.Title} event`;
  }

  return null;
}

