function findBestMatch(originalData, localResults) {
  console.log('=== FINDING BEST MATCH ===');
  console.log('Original title:', originalData.Title);
  console.log('Original address:', originalData.contact_Address);

  let bestMatch = localResults[0]; // Default to first result
  let bestScore = 0;

  localResults.forEach((result, index) => {
    const score = calculateMatchScore(originalData, result);
    console.log(`Result ${index + 1}: "${result.title}" at "${result.address}" - Score: ${score}`);

    if (score > bestScore) {
      bestScore = score;
      bestMatch = result;
    }
  });

  console.log('Best match selected:', bestMatch.title, 'with score:', bestScore);
  return bestMatch;
}

function calculateMatchScore(originalData, result) {
  let score = 0;

  // Title matching (highest priority)
  if (originalData.Title && result.title) {
    const originalTitle = originalData.Title.toLowerCase();
    const resultTitle = result.title.toLowerCase();

    // Exact match
    if (originalTitle === resultTitle) {
      score += 10;
    }
    // Contains match
    else if (resultTitle.includes(originalTitle) || originalTitle.includes(resultTitle)) {
      score += 8;
    }
    // Partial word match
    else {
      const originalWords = originalTitle.split(/\s+/);
      const resultWords = resultTitle.split(/\s+/);
      const commonWords = originalWords.filter(word => 
        word.length > 2 && resultWords.some(rWord => rWord.includes(word))
      );
      score += commonWords.length * 2;
    }
  }

  // Address matching
  if (originalData.contact_Address && result.address) {
    const originalAddress = originalData.contact_Address.toLowerCase();
    const resultAddress = result.address.toLowerCase();

    // Exact address match
    if (originalAddress === resultAddress) {
      score += 5;
    }
    // Contains match
    else if (resultAddress.includes(originalAddress) || originalAddress.includes(resultAddress)) {
      score += 3;
    }
    // Partial match
    else {
      const originalWords = originalAddress.split(/[\s,]+/);
      const resultWords = resultAddress.split(/[\s,]+/);
      const commonWords = originalWords.filter(word => 
        word.length > 2 && resultWords.some(rWord => rWord.includes(word))
      );
      score += commonWords.length;
    }
  }

  // Rating bonus (higher rated places get slight preference)
  if (result.rating && result.rating >= 4.0) {
    score += 1;
  }

  // Review count bonus (more reviews = more established)
  if (result.reviews && result.reviews >= 100) {
    score += 0.5;
  }

  console.log(`Match score breakdown for "${result.title}":`, {
    titleMatch: score >= 8 ? 'High' : score >= 4 ? 'Medium' : 'Low',
    addressMatch: score >= 5 ? 'High' : score >= 3 ? 'Medium' : 'Low',
    totalScore: score
  });

  return score;
}
